	lda     #<(_testColl)
	sta     ptr1
	lda     #>(_testColl)
	clc
	adc     _corner+1
	sta     ptr1+1
	ldy     _corner
	lda     (ptr1),y
	beq     L0505
;
; player_x = (player_x & 0xf8) + 7; // if collision, realign
;
	lda     _player_x
	and     #$F8
	clc
	adc     #$07
	sta     _player_x
;
; corner = getCollisionIndex(X1_Left_Side, Y1_Bottom); // bottom left
;
L0505:	lda     _X1_Left_Side
	jsr     pusha
	lda     _Y1_Bottom
	jsr     _getCollisionIndex
	sta     _corner
	stx     _corner+1
;
; if (testColl[corner] != 0)
;
	lda     #<(_testColl)
	sta     ptr1
	lda     #>(_testColl)
	clc
	adc     _corner+1
	sta     ptr1+1
	ldy     _corner
	lda     (ptr1),y
	beq     L0510
;
; player_x = (player_x & 0xf8) + 7; // if collision, realign
;
	lda     _player_x
	and     #$F8
	clc
	adc     #$07
L05A1:	sta     _player_x
;
; }
;
L0510:	rts

