;
	lda     _player_y
	lsr     a
	lsr     a
	lsr     a
	asl     a
	asl     a
	asl     a
	sta     ptr1
	lda     _player_x
	lsr     a
	lsr     a
	lsr     a
	ora     ptr1
	jsr     pusha
;
