;
	ldy     #$00
	lda     (sp),y
	tay
	lda     _testColl,y
	beq     L05A5
;
; colliding = 1;
;
	lda     #$01
;
; colliding = 0;
;
L05A5:	sta     _colliding
;
; }
;
	jmp     incsp1

